import { z } from "zod";

import { LOCALE_LABELS } from "@shared/domain/value-objects/locale/locale.constants";
import { LOCALE_EXAMPLES, LOCALIZED_TEXT_ENTRY_MAX_LENGTH, LOCALIZED_TEXT_ENTRY_MIN_LENGTH, LOCALIZED_TEXTS_MAX_LENGTH, LOCALIZED_TEXTS_MIN_LENGTH } from "@shared/infrastructure/http/zod/validators/localization/constants/localization.zod.validators.constants";

import type { ZodObject, ZodString, ZodOptional, ZodArray } from "zod";

import type { Locale } from "@shared/domain/value-objects/locale/locale.types";

function createZLocaleEntries<T>(entryFactory: (locale: Locale) => T): Record<Locale, T> {
  return {
    en: entryFactory("en"),
    fr: entryFactory("fr"),
    es: entryFactory("es"),
    de: entryFactory("de"),
    it: entryFactory("it"),
    pt: entryFactory("pt"),
  };
}

function zLocalizedTextEntry(locale: Locale): ZodOptional<ZodString> {
  const localeLabel = LOCALE_LABELS[locale];
  const localeExample = LOCALE_EXAMPLES[locale];

  return z.string()
    .trim()
    .min(LOCALIZED_TEXT_ENTRY_MIN_LENGTH)
    .max(LOCALIZED_TEXT_ENTRY_MAX_LENGTH)
    .optional()
    .describe(`Text in ${localeLabel}`)
    .meta({ example: localeExample });
}

function zLocalizedTextsEntry(locale: Locale): ZodOptional<ZodArray<ZodString>> {
  const localeLabel = LOCALE_LABELS[locale];
  const localeExample = LOCALE_EXAMPLES[locale];

  const zLocalizedTextsValue = z.string()
    .trim()
    .min(LOCALIZED_TEXT_ENTRY_MIN_LENGTH)
    .max(LOCALIZED_TEXT_ENTRY_MAX_LENGTH);

  return z.array(zLocalizedTextsValue)
    .min(LOCALIZED_TEXTS_MIN_LENGTH)
    .max(LOCALIZED_TEXTS_MAX_LENGTH)
    .optional()
    .describe(`Texts in ${localeLabel}`)
    .meta({ example: [localeExample] });
}

function zLocalizedText(): ZodObject<Record<Locale, ZodOptional<ZodString>>> {
  const localeEntries: Record<Locale, ZodOptional<ZodString>> = createZLocaleEntries(zLocalizedTextEntry);

  return z.strictObject(localeEntries)
    .describe("Localized text object with translations for multiple languages");
}

function zLocalizedTexts(): ZodObject<Record<Locale, ZodOptional<ZodArray<ZodString>>>> {
  const localeEntries: Record<Locale, ZodOptional<ZodArray<ZodString>>> = createZLocaleEntries(zLocalizedTextsEntry);

  return z.strictObject(localeEntries)
    .describe("Localized texts object with translations for multiple languages");
}

export {
  createZLocaleEntries,
  zLocalizedTextEntry,
  zLocalizedTextsEntry,
  zLocalizedText,
  zLocalizedTexts,
};

import { isMongoId } from "validator";
import { z } from "zod";

import { ISO_DATE_TIME_EXAMPLE, SLUG_MAX_LENGTH, SLUG_MIN_LENGTH, SLUG_REGEX } from "@shared/infrastructure/http/zod/validators/string/constants/string.zod.validators.constants";

import type { $ZodCustomParams } from "zod/v4/core";
import type { ZodString, ZodISODateTime } from "zod";

function zSlug(options: Partial<$ZodCustomParams> = {}): ZodString {
  return z.string()
    .trim()
    .min(SLUG_MIN_LENGTH)
    .max(SLUG_MAX_LENGTH)
    .regex(SLUG_REGEX, {
      error: "Invalid kebab-case value",
      ...options,
    })
    .meta({ example: "example-slug-value" });
}

function zMongoId(options: Partial<$ZodCustomParams> = {}): ZodString {
  return z.string()
    .trim()
    .refine(isMongoId, {
      error: "Invalid ObjectId value",
      ...options,
    })
    .meta({ example: "60af924f4f1a2563f8e8b456" });
}

function zIsoDateTime(options: Partial<$ZodCustomParams> = {}): ZodISODateTime {
  return z.iso.datetime({
    error: "Invalid ISO 8601 datetime value",
    ...options,
  })
    .meta({ example: ISO_DATE_TIME_EXAMPLE });
}

export {
  zSlug,
  zMongoId,
  zIsoDateTime,
};

