import { Test } from "@nestjs/testing";

import { AssignThemeToQuestionUseCase } from "@question/application/use-cases/question-theme-assignment/assign-theme-to-question/assign-theme-to-question.use-case";
import { QuestionThemeAssignmentAlreadyExistsError, QuestionThemeAssignmentCreationError } from "@question/domain/errors/question-theme-assignment/question-theme-assignment.errors";
import { FindQuestionThemeByIdUseCase } from "@question/modules/question-theme/application/use-cases/find-question-theme-by-id/find-question-theme-by-id.use-case";
import { FindQuestionByIdUseCase } from "@question/application/use-cases/find-question-by-id/find-question-by-id.use-case";
import { QUESTION_REPOSITORY_TOKEN } from "@question/domain/repositories/question.repository.constants";
import { ReferencedQuestionThemeArchivedError } from "@question/modules/question-theme/domain/errors/question-theme.errors";
import { DEFAULT_QUESTION_THEME_STATUS, QUESTION_THEME_STATUS_ARCHIVED } from "@question/modules/question-theme/domain/value-objects/question-theme-status/question-theme-status.constants";

import { createMockedFindQuestionThemeByIdUseCase } from "@mocks/contexts/question/modules/question-theme/application/use-cases/find-question-theme-by-id.use-case.mock";
import { createMockedFindQuestionByIdUseCase } from "@mocks/contexts/question/application/use-cases/find-question-by-id.use-case.mock";
import { createMockedQuestionRepository } from "@mocks/contexts/question/infrastructure/persistence/mongoose/question.mongoose.repository.mock";

import { createFakeQuestionThemeAssignmentCreationContract } from "@faketories/contexts/question/contracts/question-theme-assignment/question-theme-assignment.contracts.faketory";
import { createFakeQuestion, createFakeQuestionThemeAssignment } from "@faketories/contexts/question/entity/question.entity.faketory";
import { createFakeQuestionTheme } from "@faketories/contexts/question/question-theme/entity/question-theme.entity.faketory";
import { createFakeQuestionThemeAssignmentCreationCommand } from "@faketories/contexts/question/commands/question-theme-assignment/commands/question-theme-assignment.commands.faketory";

import type { TestingModule } from "@nestjs/testing";

import type { Question } from "@question/domain/entities/question.types";

describe("Assign Theme To Question Use Case", () => {
  let assignThemeToQuestionUseCase: AssignThemeToQuestionUseCase;
  let mocks: {
    repositories: {
      question: ReturnType<typeof createMockedQuestionRepository>;
    };
    useCases: {
      findQuestionThemeById: ReturnType<typeof createMockedFindQuestionThemeByIdUseCase>;
      findQuestionById: ReturnType<typeof createMockedFindQuestionByIdUseCase>;
    };
  };

  beforeEach(async() => {
    mocks = {
      repositories: {
        question: createMockedQuestionRepository(),
      },
      useCases: {
        findQuestionThemeById: createMockedFindQuestionThemeByIdUseCase(),
        findQuestionById: createMockedFindQuestionByIdUseCase(),
      },
    };

    const module: TestingModule = await Test.createTestingModule({
      providers: [
        AssignThemeToQuestionUseCase,
        {
          provide: FindQuestionThemeByIdUseCase,
          useValue: mocks.useCases.findQuestionThemeById,
        },
        {
          provide: FindQuestionByIdUseCase,
          useValue: mocks.useCases.findQuestionById,
        },
        {
          provide: QUESTION_REPOSITORY_TOKEN,
          useValue: mocks.repositories.question,
        },
      ],
    }).compile();

    assignThemeToQuestionUseCase = module.get<AssignThemeToQuestionUseCase>(AssignThemeToQuestionUseCase);
  });

  describe(AssignThemeToQuestionUseCase.prototype.assign, () => {
    it("should call repository.assignTheme with payload when called.", async() => {
      const command = createFakeQuestionThemeAssignmentCreationCommand({
        questionId: "q-1",
      });
      mocks.useCases.findQuestionThemeById.getById.mockResolvedValue(createFakeQuestionTheme({
        status: DEFAULT_QUESTION_THEME_STATUS,
      }));
      mocks.useCases.findQuestionById.getById.mockResolvedValue(createFakeQuestion({
        themes: [],
      }));

      await assignThemeToQuestionUseCase.assign(command);

      expect(mocks.repositories.question.assignTheme).toHaveBeenCalledExactlyOnceWith(command.questionId, command.payload);
    });

    it("should throw when repository.assignTheme returns undefined.", async() => {
      const command = createFakeQuestionThemeAssignmentCreationCommand({
        questionId: "q-1",
      });
      mocks.useCases.findQuestionThemeById.getById.mockResolvedValue(createFakeQuestionTheme({
        status: DEFAULT_QUESTION_THEME_STATUS,
      }));
      mocks.useCases.findQuestionById.getById.mockResolvedValue(createFakeQuestion());
      mocks.repositories.question.assignTheme.mockResolvedValueOnce(undefined);
      const expectedError = new QuestionThemeAssignmentCreationError(command.payload.themeId, command.questionId);

      await expect(async() => assignThemeToQuestionUseCase.assign(command)).rejects.toThrowError(expectedError);
    });

    it("should return updated question when repository.assignTheme returns it.", async() => {
      const command = createFakeQuestionThemeAssignmentCreationCommand({
        questionId: "q-1",
      });
      const updatedQuestion = createFakeQuestion();
      mocks.useCases.findQuestionThemeById.getById.mockResolvedValue(createFakeQuestionTheme({
        status: DEFAULT_QUESTION_THEME_STATUS,
      }));
      mocks.useCases.findQuestionById.getById.mockResolvedValue(createFakeQuestion());
      mocks.repositories.question.assignTheme.mockResolvedValueOnce(updatedQuestion);

      const result = await assignThemeToQuestionUseCase.assign(command);

      expect(result).toStrictEqual<Question>(updatedQuestion);
    });
  });

  describe("checkThemeIsAssignableToQuestion", () => {
    it("should throw QuestionThemeAssignmentAlreadyExistsError when theme already assigned.", async() => {
      const command = createFakeQuestionThemeAssignmentCreationCommand({
        questionId: "q-1",
        payload: createFakeQuestionThemeAssignmentCreationContract({ themeId: "theme-1" }),
      });
      const themes = [createFakeQuestionThemeAssignment({ theme: createFakeQuestionTheme({ id: "theme-1" }) })];
      const existingQuestion = createFakeQuestion({ themes });
      mocks.useCases.findQuestionById.getById.mockResolvedValue(existingQuestion);
      mocks.useCases.findQuestionThemeById.getById.mockResolvedValue(createFakeQuestionTheme({ id: command.payload.themeId }));
      const expectedError = new QuestionThemeAssignmentAlreadyExistsError(command.payload.themeId, command.questionId);

      await expect(async() => assignThemeToQuestionUseCase["checkThemeIsAssignableToQuestion"](command.payload, command.questionId)).rejects.toThrowError(expectedError);
    });

    it("should throw ReferencedQuestionThemeArchivedError when theme is archived.", async() => {
      const command = createFakeQuestionThemeAssignmentCreationCommand({
        questionId: "q-1",
      });
      mocks.useCases.findQuestionById.getById.mockResolvedValue(createFakeQuestion({ themes: [] }));
      mocks.useCases.findQuestionThemeById.getById.mockResolvedValue(createFakeQuestionTheme({
        id: command.payload.themeId,
        status: QUESTION_THEME_STATUS_ARCHIVED,
      }));
      const expectedError = new ReferencedQuestionThemeArchivedError(command.payload.themeId);

      await expect(async() => assignThemeToQuestionUseCase["checkThemeIsAssignableToQuestion"](command.payload, command.questionId)).rejects.toThrowError(expectedError);
    });
  });
});

import { Test } from "@nestjs/testing";

import { CreateQuestionUseCase } from "@question/application/use-cases/create-question/create-question.use-case";
import { GetQuestionThemesByIdsOrThrowUseCase } from "@question/modules/question-theme/application/use-cases/get-question-themes-by-ids-or-throw/get-question-themes-by-ids-or-throw.use-case";
import { QuestionCreationError } from "@question/domain/errors/question.errors";
import { QUESTION_REPOSITORY_TOKEN } from "@question/domain/repositories/question.repository.constants";
import { ReferencedQuestionThemeArchivedError } from "@question/modules/question-theme/domain/errors/question-theme.errors";
import { DEFAULT_QUESTION_THEME_STATUS, QUESTION_THEME_STATUS_ARCHIVED } from "@question/modules/question-theme/domain/value-objects/question-theme-status/question-theme-status.constants";

import { createMockedGetQuestionThemesByIdsOrThrowUseCase } from "@mocks/contexts/question/modules/question-theme/application/use-cases/get-question-themes-by-ids-or-throw.use-case.mock";
import { createMockedQuestionRepository } from "@mocks/contexts/question/infrastructure/persistence/mongoose/question.mongoose.repository.mock";

import { createFakeQuestion } from "@faketories/contexts/question/entity/question.entity.faketory";
import { createFakeQuestionCreationCommand } from "@faketories/contexts/question/commands/question.commands.faketory";
import { createFakeQuestionTheme } from "@faketories/contexts/question/question-theme/entity/question-theme.entity.faketory";

import type { TestingModule } from "@nestjs/testing";

import type { Question } from "@question/domain/entities/question.types";

describe("Create Question Use Case", () => {
  let createQuestionUseCase: CreateQuestionUseCase;
  let mocks: {
    repositories: {
      question: ReturnType<typeof createMockedQuestionRepository>;
    };
    useCases: {
      getQuestionThemesByIdsOrThrow: ReturnType<typeof createMockedGetQuestionThemesByIdsOrThrowUseCase>;
    };
  };

  beforeEach(async() => {
    mocks = {
      repositories: {
        question: createMockedQuestionRepository(),
      },
      useCases: {
        getQuestionThemesByIdsOrThrow: createMockedGetQuestionThemesByIdsOrThrowUseCase(),
      },
    };
    mocks.useCases.getQuestionThemesByIdsOrThrow.getByIdsOrThrow.mockResolvedValue([
      createFakeQuestionTheme({ status: DEFAULT_QUESTION_THEME_STATUS }),
      createFakeQuestionTheme({ status: DEFAULT_QUESTION_THEME_STATUS }),
    ]);

    const module: TestingModule = await Test.createTestingModule({
      providers: [
        CreateQuestionUseCase,
        {
          provide: GetQuestionThemesByIdsOrThrowUseCase,
          useValue: mocks.useCases.getQuestionThemesByIdsOrThrow,
        },
        {
          provide: QUESTION_REPOSITORY_TOKEN,
          useValue: mocks.repositories.question,
        },
      ],
    }).compile();

    createQuestionUseCase = module.get<CreateQuestionUseCase>(CreateQuestionUseCase);
  });

  describe(CreateQuestionUseCase.prototype.create, () => {
    it("should call repository.create with payload when called.", async() => {
      const command = createFakeQuestionCreationCommand();

      await createQuestionUseCase.create(command);

      expect(mocks.repositories.question.create).toHaveBeenCalledExactlyOnceWith(command.payload);
    });

    it("should throw QuestionCreationError when repository returns undefined.", async() => {
      const command = createFakeQuestionCreationCommand();
      mocks.repositories.question.create.mockResolvedValueOnce(undefined);
      const expectedError = new QuestionCreationError();

      await expect(createQuestionUseCase.create(command)).rejects.toThrowError(expectedError);
    });

    it("should return created question when repository returns one.", async() => {
      const command = createFakeQuestionCreationCommand();
      const expected = createFakeQuestion();
      mocks.repositories.question.create.mockResolvedValueOnce(expected);

      const actual = await createQuestionUseCase.create(command);

      expect(actual).toStrictEqual<Question>(expected);
    });

    it("should call checkQuestionIsCreatable with command when called.", async() => {
      const command = createFakeQuestionCreationCommand();
      const checkQuestionIsCreatableSpy = vi.spyOn(createQuestionUseCase as unknown as { checkQuestionIsCreatable: () => Promise<void> }, "checkQuestionIsCreatable");
      await createQuestionUseCase.create(command);

      expect(checkQuestionIsCreatableSpy).toHaveBeenCalledExactlyOnceWith(command);
    });
  });

  describe("checkQuestionIsCreatable", () => {
    it("should call getQuestionThemesByIdsOrThrow.getByIdsOrThrow with theme ids when called.", async() => {
      const command = createFakeQuestionCreationCommand();
      const expectedThemeIds = new Set(command.payload.themes.map(themeAssignment => themeAssignment.themeId));

      await createQuestionUseCase["checkQuestionIsCreatable"](command);

      expect(mocks.useCases.getQuestionThemesByIdsOrThrow.getByIdsOrThrow).toHaveBeenCalledExactlyOnceWith(expectedThemeIds);
    });

    it("should not throw when all referenced themes exist and are not archived.", async() => {
      const command = createFakeQuestionCreationCommand();

      await expect(createQuestionUseCase["checkQuestionIsCreatable"](command)).resolves.not.toThrowError();
    });

    it("should throw ReferencedQuestionThemeArchivedError when some referenced theme is archived.", async() => {
      const command = createFakeQuestionCreationCommand();
      const archivedThemeId = command.payload.themes[0].themeId;
      mocks.useCases.getQuestionThemesByIdsOrThrow.getByIdsOrThrow.mockResolvedValueOnce([
        createFakeQuestionTheme({
          id:
          archivedThemeId,
          status: QUESTION_THEME_STATUS_ARCHIVED,
        }),
      ]);
      const expectedError = new ReferencedQuestionThemeArchivedError(archivedThemeId);

      await expect(createQuestionUseCase["checkQuestionIsCreatable"](command)).rejects.toThrowError(expectedError);
    });
  });
});

import { Test } from "@nestjs/testing";

import { ArchiveQuestionUseCase } from "@question/application/use-cases/archive-question/archive-question.use-case";
import { QuestionAlreadyArchivedError, QuestionNotFoundError } from "@question/domain/errors/question.errors";
import { QUESTION_REPOSITORY_TOKEN } from "@question/domain/repositories/question.repository.constants";

import { createMockedQuestionRepository } from "@mocks/contexts/question/infrastructure/persistence/mongoose/question.mongoose.repository.mock";

import { createFakeQuestion } from "@faketories/contexts/question/entity/question.entity.faketory";

import type { Question } from "@question/domain/entities/question.types";

describe("Archive Question Use Case", () => {
  let archiveQuestionUseCase: ArchiveQuestionUseCase;
  let mocks: { repositories: { question: ReturnType<typeof createMockedQuestionRepository> } };

  beforeEach(async() => {
    mocks = { repositories: { question: createMockedQuestionRepository() } };

    const testingModule = await Test.createTestingModule({
      providers: [
        ArchiveQuestionUseCase,
        {
          provide: QUESTION_REPOSITORY_TOKEN,
          useValue: mocks.repositories.question,
        },
      ],
    }).compile();

    archiveQuestionUseCase = testingModule.get<ArchiveQuestionUseCase>(ArchiveQuestionUseCase);
  });

  describe(ArchiveQuestionUseCase.prototype.archive, () => {
    beforeEach(() => {
      mocks.repositories.question.findById.mockResolvedValue(createFakeQuestion({ status: "active" }));
    });

    it("should call repository.archive when called.", async() => {
      const id = "question-id-1";

      await archiveQuestionUseCase.archive(id);

      expect(mocks.repositories.question.archive).toHaveBeenCalledExactlyOnceWith(id);
    });

    it("should throw QuestionNotFoundError when repository.archive returns undefined.", async() => {
      const id = "question-id-2";
      mocks.repositories.question.archive.mockResolvedValueOnce(undefined);

      await expect(archiveQuestionUseCase.archive(id)).rejects.toThrowError(new QuestionNotFoundError(id));
    });

    it("should return archived question when called.", async() => {
      const id = "question-id-3";
      const archived = createFakeQuestion({ id, status: "archived" });
      mocks.repositories.question.archive.mockResolvedValueOnce(archived);

      const result = await archiveQuestionUseCase.archive(id);

      expect(result).toStrictEqual<Question>(archived);
    });
  });

  describe("throwIfQuestionNotArchivable", () => {
    it("should throw QuestionNotFoundError when question is not found.", async() => {
      const id = "non-existent-question-id";
      mocks.repositories.question.findById.mockResolvedValueOnce(undefined);

      await expect(archiveQuestionUseCase["throwIfQuestionNotArchivable"](id)).rejects.toThrowError(new QuestionNotFoundError(id));
    });

    it("should throw QuestionAlreadyArchivedError when question is already archived.", async() => {
      const id = "archived-question-id";
      mocks.repositories.question.findById.mockResolvedValueOnce(createFakeQuestion({ id, status: "archived" }));
      const expectedError = new QuestionAlreadyArchivedError(id);

      await expect(archiveQuestionUseCase["throwIfQuestionNotArchivable"](id)).rejects.toThrowError(expectedError);
    });

    it("should not throw any error when question is archivable.", async() => {
      const id = "active-question-id";
      mocks.repositories.question.findById.mockResolvedValueOnce(createFakeQuestion({ id, status: "active" }));

      await expect(archiveQuestionUseCase["throwIfQuestionNotArchivable"](id)).resolves.not.toThrowError();
    });
  });
});

