  describe("name", () => {
    it("should throw zod error when name is not a string.", () => {
      const invalid = Object.assign(validDto, { name: 123 });

      expect(() => QUESTION_AUTHOR_CREATION_DTO.parse(invalid)).toThrowError(ZodError);
