      const dtoWithWhitespace = Object.assign(validDto, { name: nameWithWhitespace });
      const parsedDto = QUESTION_AUTHOR_CREATION_DTO.parse(dtoWithWhitespace);

      expect(parsedDto.name).toBe(nameWithWhitespace.trim());
    });
  });
});

